<template>
  <div>
    Title
    <input type="text" v-model="state.title">
    Body
    <textarea v-model="state.body" @dragenter="dragEnter" @dragleave="dragLeave" @drop.prevent="dropFile"></textarea>
    PlainBody
    <Button @click.native="postman">POST</Button>

    <Button @click.native="uploadImage">UploadImage</Button>

    <Post v-bind:body="state.body"></Post>
  </div>
</template>

<script lang="ts">

import {defineComponent, reactive, computed} from "@nuxtjs/composition-api";
import {ContentsApi, PostsApi} from "~/client";
import {buildConfiguration} from "~/client/configurationFactory";
import Button from "~/components/atoms/Button.vue"
import Post from "~/components/molecules/Post.vue"
import { createMarkdown } from "safe-marked";
const markdown = createMarkdown();
const { htmlToText } = require('html-to-text');

type Props = {
  isPosting: boolean
}

export default defineComponent({
  components: {
    Button,
    Post
  },
  name: "post",
  middleware: 'auth',
  setup(props:Props, context) {
    props.isPosting = false
    const state = reactive({
      title: "Hello world",
      body: "# Hello World ## This is power",
      renderedBody: computed(() => markdown(state.body)),
      plainBody: computed(() => htmlToText(state.renderedBody))
    });

    const dragEnter = () => {
      console.log("dragenter")
    }
    const dragLeave = () => {
      console.log("dragleave")
    }
    const dropFile = async (event) => {
     const file = event.dataTransfer.files[0];
     const reader = new FileReader();
     reader.onload = async () => {
       const uploader = new ContentsApi(buildConfiguration());
       const result = await uploader.contentsPost({image: reader.result as string});
       console.log(result.data.url);
     }
     reader.readAsDataURL(file);
    }

    const uploadImage = async() => {
      const uploader = new ContentsApi(buildConfiguration());
      const result = await uploader.contentsPost({image: "data:image/jpeg;base64,"})
      console.log(result.data.url)
    };

    const postman = async () => {
      if (props.isPosting)
        return;
      props.isPosting = true;
      const api = new PostsApi(buildConfiguration());
      await api.postsPost({title: state.title, body: state.body, plain_body: state.plainBody, tags: []}).then(res => {
        // TODO:
        context.root.$router.push(`/posts/${res.data.post_id}`)
      }).catch(err => {
        // TODO:
        console.log(err)
        console.log(err.response.data)
        props.isPosting = false;
      });
    }

    return {
      props,
      state,
      postman,
      uploadImage,

      dragEnter,
      dragLeave,
      dropFile
    }
  }
})
</script>

